mapper.select:
SELECT * FROM ( SELECT rownum as rnum, C.* FROM ( SELECT A.no, A.title, A.member_no as memberNo, B.name as memberName, A.view_cnt as viewCount, A.depth, to_char( A.reg_date, 'yyyy-mm-dd hh:mi:ss' ) as regDate FROM board A, member B WHERE A.member_no = B.no
AND ( A.title LIKE '%${searchKeyword }%' OR A.content LIKE '%${searchKeyword }%' )
ORDER BY group_no DESC, order_no ASC ) C ) WHERE ( #{page } - 1 ) * #{pageSize } + 1 <= rnum AND rnum <= #{page } * #{pageSize }


select no, title, content, member_no as memberNo, group_no as groupNo, order_no as orderNo, depth
  from board
 where no=#{no }

mapper.delete:

delete
  from board
 where no = #{no }
   and member_no = #{memberNo }

mapper.update:
		
		update board
		   set title = #{title }, content = #{content }
         where no = #{no }
           and member_no = #{memberNo }

		
		update board
		   set view_cnt = view_cnt + 1		
         where no = #{no }

		
		update board
		   set order_no = order_no + 1		
         where order_no > #{parentOrderNo }
