mapper.delete:

delete
  from board
 where no = #{no }
   and member_no = #{memberNo }

mapper.update:
		
		update board
		   set title = #{title },
			   content = #{content }
         where no = #{no }
           and member_no = #{memberNo }

		
		update board
		   set view_cnt = view_cnt + 1		
         where no = #{no }
